PREFIX schema: <https://schema.org/>

SELECT (COUNT(?record) AS ?count) ?name ?lat ?lon ?glacier WHERE {
  ?glacier a schema:Landform ;
           schema:name ?name ;
           schema:longitude ?lon ;
           schema:latitude ?lat .
  ?record schema:spatial ?glacier ;
          schema:provider ?provider .
} GROUP BY ?name ?lat ?lon ?glacier

PREFIX schema: <https://schema.org/>
PREFIX wdt: <http://www.wikidata.org/prop/direct/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX wd: <http://www.wikidata.org/entity/>

SELECT ?label (COUNT(?label) AS ?count) WHERE {
  ?landform a schema:Landform ;
            rdfs:seeAlso ?wd .

  SERVICE <https://query.wikidata.org/bigdata/namespace/wdq/sparql> {
    ?wd wdt:P30 ?continent .
    ?continent rdfs:label ?label .
    FILTER (lang(?label) = "en")
  }
} GROUP BY ?label

PREFIX dcterms: <http://purl.org/dc/terms/>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

SELECT ?label (COUNT(?label) AS ?count) WHERE {
  ?record a/skos:broader* <https://www.glaciersinarchives.org/id/type/work> .
  ?record dcterms:rights ?label .
} GROUP BY ?label

PREFIX schema: <https://schema.org/>

SELECT ?label (COUNT(?label) AS ?count) WHERE {
  ?record schema:provider ?label
} GROUP BY ?label
